import 'bootstrap/dist/css/bootstrap.css';
import Bckground from '../images/scattered-forcefields.png'

const ViewQuestion = ({ title, body, createTime, tags }) => (
    <div style={{
        backgroundImage: `url(${Bckground})`,
        height: '100%',
        width: "100%"
    }}>
        <input type="button" value="Click Me" style="float: right;"/>
        <div class="card w-75 " style={{
            margin: 'auto',
            width: '50%',
            border: '3px',
            padding: '10px',
            background:'transparent'
        }}>
            <div class="card-body">
                <h5 class="card-title">{title}</h5>
                <p class="card-text">{body}</p>
                <p>{createTime}</p>
                <div class="btn-group" role="group" aria-label="Basic example">
                    {
                        tags.map((tag, index) => (
                            <tr key={index}>
                                <button type="button" class="btn btn-primary" style={{ marginLeft:"3px" }}>{tag}</button>
                            </tr>
                        ))
                    }
                </div>
            </div>
            <div>
            </div>

        </div>
    </div>
);
export default ViewQuestion;
