import 'bootstrap/dist/css/bootstrap.css';
import Bckground from '../images/scattered-forcefields.png'

const ViewQuestion = ({ title, body, createTime, tags, answares }) => (
    <div style={{
        backgroundImage: `url(${Bckground})`,
        height: '100%',
        width: "100%"
    }}>
        <div style={{
            float: 'right',
            marginRight:'5px'
        }}>
            <button type="button" class="btn btn-primary" style={{ marginLeft: "3px", marginTop:"3px" }}>Add Answare</button>
        </div>
        <div class="card w-75 " style={{
            margin: 'auto',
            width: '50%',
            border: '3px',
            padding: '10px',
            background:'transparent'
        }}>
            <div class="card-body" style={{ borderBottomColor:'#191970', borderBottom:'solid'}}>
                <h5 class="card-title">{title}</h5>
                <p class="card-text">{body}</p>
                <p>{createTime}</p>
                <div class="btn-group" role="group" aria-label="Basic example">
                    {
                        tags.map((tag, index) => (
                            <tr key={index}>
                                <button type="button" class="btn btn-primary" style={{ marginLeft:"3px" }}>{tag}</button>
                            </tr>
                        ))
                    }
                </div>
            </div>
            <ul class="list-group" style={{background:"transparent"}}>
                {
                    answares.map((answare, index) => (
                        <tr key={index}>
                            <li class="list-group-item"><p>{answare.text}</p>
                                <p>{answare.authorUsername}</p>
                                <p>{answare.createTime}</p>
                            </li>
                        </tr>
                    ))
                }
            </ul>

        </div>
    </div>
);
export default ViewQuestion;
