import 'bootstrap/dist/css/bootstrap.css';
import Bckground from '../images/tile_background.png'

const ViewQuestion = ({ title, body, createTime, tags }) => (
    <div class ="container" style={{
        backgroundImage: `url(${Bckground})`,
        paddingLeft: 200,
        height:'100%'
    }}>
        <div class="card w-75">
            <div class="card-body">
                <h5 class="card-title">{title}</h5>
                <p class="card-text">{body}</p>
                <p>{createTime}</p>
            </div>
            <ul class="list-group list-group-flush">
                {
                    tags.map((tag, index) => (
                        <tr key={index}>
                            <button type="button" class="btn btn-primary">{tag}</button>
                        </tr>
                    ))
                }

            </ul>

        </div>
    </div>
);
export default ViewQuestion;
