class Question {
    constructor() {
        this.state = {
            questions: [{
                title: "Title1",
                body: "Body1",
                createTime: "2022-03-21 16:01:17",
                authorId: 1,
                tags: ["java","tag"]
            },
            {
                title: "Title12",
                body: "Body2",
                createTime: "2022-03-21 16:01:17",
                authorId: 1,
                tags: ["java"]
            }
            ]
        };
    }

    addTagToQuestion(index,tag) {
        this.state.questions[index].tags = [
            ...this.state.questions[index].tags,
            tag
        ];
    }

    addQuestion(newQuestion) {
        this.state.questions = [
            ...this.state.questions,
            {
                ...newQuestion,
                tags: []
            }
        ];
    }

    getQuestion() {
        return this.state.questions;
    }

    getQuestionByText(text) {
        return this.state.questions.filter(item => item.body.includes(text) || item.tags.includes(text) || item.title.includes(text));
    }

    getQuestion
}
const QuestionInstance = new Question();
export default QuestionInstance;
