import QuestionPresenterInstance from '../presenter/QuestionPresenter'
import AppPresenterInstance from '../presenter/AppPresenter'
import TagPresenterInstance from '../presenter/TagPresenter'
import CreateQuestion from '../presentational/CreateQuestion'
import { useState } from 'react';

function SmartCreateQuestion() {
    const [question, setQuestion] = useState({
        title: "",
        body: "",
        author: "",
        createTime: "",
        tags: ["hello"]
    });
    const allTags = TagPresenterInstance.getTags();


    const onChange = (property, newValue) => {
        setQuestion({
            ...question,
            [property]: newValue
        });
    }

    const onAddTag = (tag) => {
        setQuestion({
            ...question,
            tags: [...question.tags, tag]
        });
    }

    const onRemoveTag = (tag) => {
        question.tags.splice(question.tags.indexOf(tag), 1);
    }

    const onSave = () => {
        setQuestion({
            ...question,
            createTime: Date.now().toString,
            author: AppPresenterInstance.getLoggedUser()
        });
        QuestionPresenterInstance.addQuestion(question);
        window.location.assign("#/home");
    }

    return (<CreateQuestion title={question.title} body={question.body} tags={question.tags} allTags={allTags} onChange={onChange} onRemoveTag={onRemoveTag} onAddTag={onAddTag} onSave={onSave} />
    );
}
export default SmartCreateQuestion;
