import QuestionPresenterInstance from '../presenter/QuestionPresenter'
import AppPresenterInstance from '../presenter/AppPresenter'
import AppPresneter from "../presenter/AppPresenter"
import Home from '../presentational/Home'
import VotesPresenterInstance from '../presenter/VotesPresenter'
import { useEffect, useState } from 'react';
import RestClient from '../rest/RestClient';

function SmartHome() {
    const [text, setNewText] = useState({
        text:""
    });
    const [questions, setNewQuestions] = useState({
        questions: []
    });

    useEffect(() => {
        const credentials = AppPresenterInstance.getLoggedUser()
        console.log(credentials.username)
        console.log(credentials.password)
        QuestionPresenterInstance.getQuestion("").then(foundQuestions => {
            //console.log(client)
            setNewQuestions({
                questions: [...foundQuestions]
            }
            )
        });
    }, []);


    const onChange = (property, newValue) => {
        setNewText({
            [property]: newValue
        });
    }
    const onSearch = () => {
        QuestionPresenterInstance.getQuestionByText(text.text).then(foundQuestions => {
            setNewQuestions({
                questions: [...foundQuestions]
            })
        });

    }

    const onSelect = (title) => {
        QuestionPresenterInstance.getQuestion().then(foundQuestions => {
            setNewQuestions({
                questions: [...foundQuestions]
            }
            )
        });
        var index = questions.questions.indexOf(questions.questions.find(element => element.title === title))
        window.location.assign("/#/question-details/" + index)
    }

    const onVote = (ud, title, body, user, createTime, upvotes, downvotes) => {
        const questionsi = questions.questions
        const quest = questionsi.find(q => q.title === title && q.body === body && q.createTime === createTime && q.author === user && q.upvotes === upvotes && q.downvotes === downvotes );
        if (ud === "up") {
            QuestionPresenterInstance.upvote(quest).then(foundQuestions => {
                setNewQuestions({
                    questions: [...foundQuestions]
                })
            });
            //console.log(questions.questions);

            } else {
                if (ud === "down") {
                    QuestionPresenterInstance.downvote(quest).then(foundQuestions => {
                        setNewQuestions({
                            questions: [...foundQuestions]
                        })
                    });

                }
        }
    }

    return (
        <Home text={text.text}
            questions={questions.questions}
            onSearch={onSearch}
            onChange={onChange}
            onSelect={onSelect}
            onVote={onVote}
            />
        );
}
export default SmartHome;
