import QuestionInstance from '../model/Question';
import { EventEmitter } from "events";
import RestClient from '../rest/RestClient';

const client = new RestClient("username1", "password");
class QuestionPresenter extends EventEmitter {
    constructor() {
        super();
        QuestionInstance.on("QuestionAdded", event => {
            this.emit("QuestionAdded", event);
        })
    }

    addTagToQuestion(index, tag) {
        return QuestionInstance.addTagToQuestion(index, tag);
    }
    addQuestion(newQuestion) {
        return QuestionInstance.addQuestion(newQuestion);
    }
    async getQuestion() {
         questions = await client.loadAllStudents();
        QuestionInstance.addQuestion(questions);
        return QuestionInstance.getQuestion();
    }

    getQuestionByText(text) {
        return QuestionInstance.getQuestionByText(text);
    }

    getQuestionById(id) {
        return QuestionInstance.getQuestionbyId(id);
    }

    upvote(index) {
        return QuestionInstance.upvote(index);
    }

    downvote(index) {
        return QuestionInstance.downvote(index);
    }

    getVotes(index) {
        return QuestionInstance.getVotes(index);
    }
}
const QuestionPresenterInstance = new QuestionPresenter();
export default QuestionPresenterInstance;
