import { EventEmitter } from "events"
import WebSocketListenerInstance from "../ws/WebSocketListener"

class Question extends EventEmitter{
    constructor() {
        super();
        this.state = {
            questions: []
        };
        WebSocketListenerInstance.on("QUESTION_UPDATED", event => {
            QuestionInstance.upvote(event.questionDto);
            this.emit("q_updated", this.state.questions);
        })
    }

    addTagToQuestion(index,tag) {
        this.state.questions[index].tags = [
            ...this.state.questions[index].tags,
            tag
        ];
    }

    addQuestion(questions) {
        this.state.questions = [
            ...questions
        ];
    }

    getQuestion() {
        return this.state.questions;
    }

    removeQuetions() {
        this.state.questions = [];
    }


    getQuestionbyId(id) {
        return this.state.questions[id];
    }

    upvote(question) {
        console.log("before " + index + "  " + this.state.questions[index].upvotes)
        const index = this.state.questions.findIndex(item => item.body === question.body && item.title === question.title && item.createTime === question.createTime && item.author === question.author && item.upvotes === question.upvotes && question.downvotes === item.downvotes);
        this.state.questions[index].upvotes = this.state.questions[index].upvotes + 1;

    }

    downvote(index) {
        this.state.questions[index].downvotes = this.state.questions[index].downvotes + 1
    }

    getVotes(index) {
        var votes = this.state.questions[index].upvotes - this.state.questions[index].downvotes
        return votes
    }
}
const QuestionInstance = new Question();
export default QuestionInstance;
