import QuestionPresenterInstance from '../presenter/QuestionPresenter'
import AppPresenterInstance from '../presenter/AppPresenter'
import Home from '../presentational/Home'
import VotesPresenterInstance from '../presenter/VotesPresenter'
import { useState } from 'react';
import UserPresenterInstance from '../presenter/UserPresenter';

function SmartHome() {
    const [text, setNewText] = useState({
        text:AppPresenterInstance.getLoggedUser()
    });
    const [questions, setNewQuestions] = useState({
        questions: QuestionPresenterInstance.getQuestion()
    });

    const onChange = (property, newValue) => {
        setNewText({
            [property]: newValue
        });
    }
    const onSearch = () => {
        setNewQuestions({
            questions: QuestionPresenterInstance.getQuestionByText(text.text)
        });

    }

    const onSelect = (title) => {
        var questionsi = QuestionPresenterInstance.getQuestion()
        var index = questionsi.indexOf(questionsi.find(element => element.title === title))
        window.location.assign("/#/question-details/" + index)
    }

    const onVote = (ud, text) => {
        var questionsi = QuestionPresenterInstance.getQuestion()
        var ind = questionsi.indexOf(questionsi.find(element => element.title === text))
        var userVotes = VotesPresenterInstance.getVotesBy("Username1")
        console.log(userVotes.length)
        var vote = userVotes.find(item => item.id === ind && item.type === "question")
        if (vote === undefined) {
            if (ud === "up") {
                console.log("am ajuns aici pt up")
                QuestionPresenterInstance.upvote(ind)

            } else {
                if (ud === "down") {
                    console.log("am ajuns aici pt down")
                    QuestionPresenterInstance.downvote(ind)
                }
            }
            VotesPresenterInstance.addVote({
                user: UserPresenterInstance.getLoggedUser(),
                type: "question",
                id: ind
            });
            setNewQuestions({
                questions: QuestionPresenterInstance.getQuestion()
            });
        } else {
            console.log("am ajuns aici")
        }
    }

    return (
        <Home text={text.text}
            questions={questions.questions}
            onSearch={onSearch}
            onChange={onChange}
            onSelect={onSelect}
            onVote={onVote}
            />
        );
}
export default SmartHome;
