import UserPresenter from '../presenter/UserPresenter'
import AppPresenter from '../presenter/AppPresenter'
import LogIn from '../presentational/LogIn'
import { useState } from 'react'

function SmartLogIn() {
    const [newUser, setNewUser] = useState({
        username: "Username",
        password:"Password"
    });
    const [text, setNewText] = useState({
        text:""
    });

    const onChange = (property, newValue) => {
        setNewUser({
            ...newUser,
            [property]: newValue
        });
    }
    const onLogIn = () => {
        var user = UserPresenter.getUserByUsername(newUser.username)[0];
        if (user != null) {
            if (user.password == newUser.password) {
                AppPresenter.setLoggedUserId(user.username);
            } else {
                setNewText({
                    text: "Incorect password"
                });
            }
        } else {
            setNewText({
                text: "Username not found"
            });
        }
        window.location.assign("#/home");
    }

    return (
        <LogIn 
            username={newUser.username}
            password={newUser.password}
            onLogIn={onLogIn}
            onChange={onChange}
            text={text.text} />
        );


}
export default SmartLogIn;
