import ViewQuestion from '../presentational/ViewQuestion'
import AnswarePresenterInstance from '../presenter/AnswarePresenter'
import QuestionPresenterInstance from '../presenter/QuestionPresenter'
import AppPresenterInstance from '../presenter/AppPresenter'
import VotesPresenterInstance from '../presenter/VotesPresenter'
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import moment from 'moment'


function SmartViewQuestion() {
    const [question, setQuestion] = useState({
        title: "",
        body: "",
        tags: [],
        createTime: "",
        upvotes: 0,
        downvotes: 0
    });
    const { index } = useParams();

    const [text, setText] = useState({
        text: "",
        authorUsername: "Username1",
        createTime: "",
        referenceQuestion: index
    });

    const [chAnsware, setChAnsware] = useState({
        text: ""
    });

    const onChAnsware = (newValue) => {
        setChAnsware({
            text:newValue
        });
    }

    const [answares, setAnswares] = useState({
        answares: AnswarePresenterInstance.getAnsware(index)
    });

    useEffect(() => {
        setQuestion(QuestionPresenterInstance.getQuestionById(index))
    }, []);


    const onChangeTextArea = (property, newValue) => {
        setText({
            ...text,
            [property]: newValue
        });
    }



    const onAddAnsware = () => {
        var date = new Date()
        setText({
            ...text,
            createTime: moment(date).format("YYYY-MM-DD HH:MM:SS")
        });
        AnswarePresenterInstance.addAnsware(text);
        setAnswares({
            answares: AnswarePresenterInstance.getAnsware(index)
        })
        
    }

    const onOpenEditAnsware = (proprety,newValue) => {
        setText({
            ...text,
            [proprety]: newValue
        });
    }

    const onEditAnsware = () => {
        var answaresi = AnswarePresenterInstance.getAnswares()
        var ind = answaresi.indexOf(answaresi.find(element => element.text === chAnsware.text))
        console.log(ind)
        AnswarePresenterInstance.editAnsware(ind, text.text);
        setAnswares({
            answares: AnswarePresenterInstance.getAnsware(index)
        })
    }

    const onVote = (ud, type, text) => {
        if (type === "question") {
            var userVotes = VotesPresenterInstance.getVotesBy("Username1")
            var vote = userVotes.find(item => item.id === index && item.type === "question")
            if (vote === undefined) {
                if (ud === "up") {
                    console.log("am ajuns aici pt up")
                    QuestionPresenterInstance.upvote(index)
                    VotesPresenterInstance.addVote({
                        user: "Username1",
                        type: "question",
                        id: index
                    });
                    setQuestion(QuestionPresenterInstance.getQuestionById(index));

                } else {
                    if (ud === "down") {
                        console.log("am ajuns aici pt down")
                        QuestionPresenterInstance.downvote(index)

                        VotesPresenterInstance.addVote({
                            user: "Username1",
                            type: "question",
                            id: index
                        });
                        setQuestion(QuestionPresenterInstance.getQuestionById(index))
                    }
                }
            }
        } else {
            var answaresi = AnswarePresenterInstance.getAnswares()
            var ind = answaresi.indexOf(answaresi.find(element => element.text === text))
            var userVotes = VotesPresenterInstance.getVotesBy("Username1")
            var vote = userVotes.find(item => item.id === ind && item.type === "answare")
            if (vote === undefined) {
                if (ud === "up") {
                    console.log("am ajuns aici pt up")
                    AnswarePresenterInstance.upvote(ind)
                    VotesPresenterInstance.addVote({
                        user: "Username1",
                        type: "answare",
                        id: ind
                    });
                    setAnswares({
                        answares: AnswarePresenterInstance.getAnsware(index)
                    })

                } else {
                    if (ud === "down") {
                        console.log("am ajuns aici pt down")
                        AnswarePresenterInstance.downvote(ind)

                        VotesPresenterInstance.addVote({
                            user: "Username1",
                            type: "answare",
                            id: ind
                        });
                        setAnswares({
                            answares: AnswarePresenterInstance.getAnsware(index)
                        })
                    }
                }
            }
        }
    }

    return (<ViewQuestion title={question.title}
        body={question.body}
        createTime={question.createTime}
        tags={question.tags}
        answares={answares.answares}
        text={text.text}
        onChangeTextArea={onChangeTextArea}
        onAddAnsware={onAddAnsware}
        onOpenEditAnsware={onOpenEditAnsware}
        onEditAnsware={onEditAnsware}
        onChAnsware={onChAnsware}
        onVote={onVote}
        upvotes={question.upvotes}
        downvotes={question.downvotes}
    />
    );
};
export default SmartViewQuestion
