import QuestionPresenterInstance from '../presenter/QuestionPresenter'
import Home from '../presentational/Home'
import { useState } from 'react';

function SmartHome() {
    const [text, setNewText] = useState({
        text:""
    });
    const [questions, setNewQuestions] = useState({
        questions: QuestionPresenterInstance.getQuestion()
    });

    const onChange = (property, newValue) => {
        setNewText({
            [property]: newValue
        });
    }
    const onSearch = () => {
        setNewQuestions({
            questions: QuestionPresenterInstance.getQuestionByText(text.text)
        });

    }

    const onSelect = (title) => {
        console.log(index)
        var questionsi = QuestionPresenterInstance.getQuestion()
        var index = questionsi.indexOf(questionsi.find(element => element.title === title))
        window.location.assign("/#/question-details/" + index)
    }

    return (
        <Home text={text.text}
            questions={questions.questions}
            onSearch={onSearch}
            onChange={onChange}
            onSelect={onSelect}
            />
        );
}
export default SmartHome;
